package programs.day1;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i = 1, sum = 0;

	        while (i <= 10) {
	            sum = sum + i;
	            i++;
	        }

	        System.out.println("Sum = " + sum);

	

	}

}
